
public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Swapping Two Variables

		String x ="water";
		String y = "Kool-aid";
		String temp;
		temp = x;
		x=y;
		y=temp;
		
		System.out.println("x: "+x);
		System.out.println("y: "+y);

		
		
	}

}
